package MyFirstProject;

public class PrimeOrNot {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int n=19;
		if(n%2==0)
		{
			System.out.println(n+" Number is not prime");
		}
		else {
			System.out.println(n+" Number is  prime");
		}
		
	}

}
